import React, { useEffect, useState } from 'react';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';
import { useDispatch } from 'react-redux';
import { toast } from 'react-toastify';
import { addItemCartStart } from '../../action/ShopCartAction';
import './InfoDetailProduct.scss';
import CommonUtils from '../../utils/CommonUtils';
function InfoDetailProduct(props) {

    const fakeDataProduct = {
        id: 1,
        name: 'Bo mạch Arduino',
        categoryData: { value: 'Loại bo mạch Arduino' },
        // Thêm các thuộc tính khác nếu cần thiết
        contentHTML:"Arduino Leonardo R3 (Leonardo) là một board mạch vi điều khiển dựa trên vi điều khiển  ATmega32U4. Nó có 20 cổng vào/ra số (trong đó có 7 cổng có thể sử dụng ở chế độ băm xung PWM và 12 cổng dùng như đầu vào/ra tương tự), board mạch Leonardo còn hàn sẵn thạch anh 16Mhz, một cổng Micro USB, một Jack nguồn, một cổng ICSP, và một nút nhấn Reset. Với Leonardo ta có tất cả cho hệ vi điều khiển; Kết nối đơn giản với máy tính với một cáp Micro USB, và có thể sử dụng nguồn DC, hay với chỉ một nguồn pin/acquy. Điểm đặc biệt Leonardo khác biệt với các thế hệ board Arduino đi trước bởi sự tích hợp cổng kết nối USB (CDC – Cổng com ảo) bên trong chip Atmega32U4, điều đó đã giúp loại bỏ các chíp đi kèm làm nhiệm vụ kết nối với cổng USB, cho phép Leonard kết nối với máy tính đơn giản như bàn phím hay chuột."
    }
    
    const fakeArrDetail = {
        productImage: [
            {id: 1, image:"https://product.hstatic.net/1000069225/product/d4d5cc2c-47d3-4e11-855f-79f43022ac0e_1_201_a_66cfb1b824af4bb591d4cbb180e64e93_large.jpg"},
            {id: 2, image:"https://product.hstatic.net/1000069225/product/d4d5cc2c-47d3-4e11-855f-79f43022ac0e_1_201_a_66cfb1b824af4bb591d4cbb180e64e93_large.jpg"},
            {id: 3, image:"https://product.hstatic.net/1000069225/product/d4d5cc2c-47d3-4e11-855f-79f43022ac0e_1_201_a_66cfb1b824af4bb591d4cbb180e64e93_large.jpg"},
            {id: 4, image:"https://product.hstatic.net/1000069225/product/d4d5cc2c-47d3-4e11-855f-79f43022ac0e_1_201_a_66cfb1b824af4bb591d4cbb180e64e93_large.jpg"},
            {id: 5, image:"https://product.hstatic.net/1000069225/product/d4d5cc2c-47d3-4e11-855f-79f43022ac0e_1_201_a_66cfb1b824af4bb591d4cbb180e64e93_large.jpg"},
        ],
        discountPrice: 150000,
        productDetailSize: [
            { id: 1, sizeData: { value: 'S' }, stock: 10 },
            { id: 2, sizeData: { value: 'M' }, stock: 5 }
            // Thêm các phần tử khác nếu cần thiết
        ],
        description: 'Arduino Leonardo R3 (Leonardo) là một board mạch vi điều khiển dựa trên vi điều khiển  ATmega32U4. Nó có 20 cổng vào/ra số (trong đó có 7 cổng có thể sử dụng ở chế độ băm xung PWM và 12 cổng...'
    }
        
    let { dataProduct } = props
    let [arrDetail, setarrDetail] = useState([])
    const [productDetail, setproductDetail] = useState([])
    const [isOpen, setisOpen] = useState(false)
    const [imgPreview, setimgPreview] = useState('')
    const [activeLinkId, setactiveLinkId] = useState('')
    const [quantity, setquantity] = useState('')
    const [quantityProduct, setquantityProduct] = useState(1)
    useEffect(() => {

        let { productDetail } = dataProduct ? dataProduct : []

        if (productDetail) {
            setproductDetail(productDetail)

            setarrDetail(productDetail[0])
            setactiveLinkId(productDetail[0].productDetailSize[0].id)
            setquantity(productDetail[0].productDetailSize[0].stock)

            props.sendDataFromInforDetail(productDetail[0].productDetailSize[0])
        }
    }, [props.dataProduct])

    let handleSelectDetail = (event) => {
        setarrDetail(productDetail[event.target.value])
        if (productDetail[event.target.value] && productDetail[event.target.value].productDetailSize.length > 0) {
            setactiveLinkId(productDetail[event.target.value].productDetailSize[0].id)
            setquantity(productDetail[event.target.value].productDetailSize[0].stock)
            props.sendDataFromInforDetail(productDetail[event.target.value].productDetailSize[0])
        }

    }
    let openPreviewImage = (url) => {


        setimgPreview(url);
        setisOpen(true);

    }
    let handleClickBoxSize = (data) => {

        setactiveLinkId(data.id)
        setquantity(data.stock)
        props.sendDataFromInforDetail(data)
    }
    const dispatch = useDispatch()
    let handleAddShopCart = () => {
        if (props.userId) {
            dispatch(addItemCartStart({
                userId: props.userId,
                productdetailsizeId: activeLinkId,
                quantity: quantityProduct,
            }))
        } else {
            toast.error("Đăng nhập để thêm vào giỏ hàng")
        }

    }
    return (


        <div className="row s_product_inner">
            <div className="col-lg-6">
                <div className="s_product_img">
                    <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
                        <div>
                            <ol className="carousel-indicators">
                                {/* {arrDetail && arrDetail.productImage && arrDetail.productImage.length > 0 &&
                                    arrDetail.productImage.map((item, index) => {
                                        if (index === 0) {
                                            return (
                                                <li data-target="#carouselExampleIndicators" data-slide-to={index} className="active">
                                                    <img height="60px" className="w-100" src={item.image} alt="" />
                                                </li>
                                            )
                                        } else {
                                            return (
                                                <li data-target="#carouselExampleIndicators" data-slide-to={index} className="">
                                                    <img height="60px" className="w-100" src={item.image} alt="" />
                                                </li>
                                            )
                                        }

                                    })
                                } */}

                                {/* Vinh hình ảnh sản phẩm */}
                                {fakeArrDetail && fakeArrDetail.productImage && fakeArrDetail.productImage.length > 0 &&
                                    fakeArrDetail.productImage.map((item, index) => {
                                        if (index === 0) {
                                            return (
                                                <li data-target="#carouselExampleIndicators" data-slide-to={index} className="active">
                                                    <img height="60px" className="w-100" src={item.image} alt="" />
                                                </li>
                                            )
                                        } else {
                                            return (
                                                <li data-target="#carouselExampleIndicators" data-slide-to={index} className="">
                                                    <img height="60px" className="w-100" src={item.image} alt="" />
                                                </li>
                                            )
                                        }

                                    })
                                }

                            </ol>
                        </div>
                        <div className="carousel-inner">

